mapper.update:
update sys_dictionary
set code = #{code,jdbcType=INTEGER},
  codeflag = #{codeflag,jdbcType=INTEGER},
  codeflagname = #{codeflagname,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, code, codeflag, codeflagname
from sys_dictionary
where id = #{id,jdbcType=INTEGER}
